namespace Magezon\ProductAttachments\Model\Config\Source;

class ListDisplay implements \Magento\Framework\Option\ArrayInterface
{
    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray()
    {
        return [['value' => 1, 'label' => __('Group By Category')], ['value' => 0, 'label' => __('List')]];
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return [1 => __('Group By Category'), 0 => __('List')];
    }
}
